import styled from "styled-components";

export const CardPostCard = styled.a`
    border-radius: 6px;
    overflow: hidden;
    background-color: #fff;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.035),
        0 4px 25px rgba(0, 0, 0, 0.07);
    color: black;
    text-decoration: none;
    transition: all 0.2s;

    &:hover {
        color: black;
        box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.035),
            0 6px 35px rgba(0, 0, 0, 0.2);
        transform: translateY(-2px);
    }
`;

export const FigurePostCard = styled.figure`
    padding: 56% 0 0;
    margin: 0;
    vertical-align: center;
    max-width: 100%;
    position: relative;
    overflow: hidden;
    border-radius: 6px 6px 0 0;

    img {
        max-width: 100%;
        position: absolute;
        top: 0;
        left: 0;
    }
`;

export const TitlePostCard = styled.h3`
    margin: 10px 20px;
    font-size: 1.4rem;
`;

export const LeadPostCard = styled.div`
    margin: 0 20px 20px;

    & > * {
        margin: 0 0 10px;
    }
`;
